mapper.select:
select * from audition_cv
where audition_cv_type like concat('%', #{searchValue}, '%') where audition_cv_name like concat('%', #{searchValue}, '%') where audition_cv_gender like concat('%', #{searchValue}, '%')
and audition_cv_regdate >= #{startDate}
and audition_cv_regdate <= date_add(#{endDate}, interval 1 day)
order by audition_cv_no desc limit #{pageStart}, #{amount}

mapper.update:
update audition_cv
set audition_cv_progress = '2차'
where audition_cv_no = #{audition_cv_no}

update audition_cv
set audition_cv_progress = '1차 불합격'
where audition_cv_no = #{audition_cv_no}
